#include "main.h"
                                                                                                                       /**                                                                                                                     * find_sqrt - Helper function to find the square                                                                       * root using binary search.                                                                                            * @n: The number for which to find the square root.                                                                    * @start: The starting point of the search range.                                                                      * @end: The ending point of the search range.                                                                          * Return: The natural square root of n,                                                                                * or -1 if not found.                                                                                                  */                                                                                                                    int find_sqrt(int n, int start, int end)                                                                               {
 	int mid;

	if (start > end)                                                                                                               return -1;                                                                                                                                                                                                                   
	mid = start + (end - start) / 2;                                                                                                                                                                                                          if (mid * mid == n)                                                                                                            return mid;                                                                                                                                                                                                                           if (mid * mid > n)                                                                                                             return find_sqrt(n, start, mid - 1);                                                                                                                                                                                                  return find_sqrt(n, mid + 1, end);                                                                             }
